import React, { useReducer } from 'react';
import { View, StyleSheet, Text, Button } from 'react-native';
import ColorCounter from '../components/ColorCounter';

const COLOR_INCREMENT = 15;

const reducer = (state, action) => {
    // state === { red: number, blue: number, green: number };
    // action === { colorToCgange: 'red' || 'blue' || 'green', amount: 15 || -15 };

    switch (action.colorToCgange) {
        case 'red':
            if (state.red + action.amount > 255 || state.red + action.amount < 0) {
                return state;
            }
            return {...state, red: state.red + action.amount};
        case 'blue':
            if(state.blue + action.amount > 255 || state.blue + action.amount < 0) {
                return state;
            }
            return {...state, blue: state.blue + action.amount};
        case 'green':
            if (state.green + action.amount > 255 || state.green + action.amount < 0) {
                return state;
            }
            return {...state, green: state.green + action.amount};

        default:
            return state;
    }
}

const SquareScreen = () => {

    const [state, dispatch] = useReducer(reducer, { red: 0, blue: 0, green: 0 });
    const { red, blue, green } = state;

    return (
        <View>
            <ColorCounter 
                onIncrease={() => dispatch({ colorToCgange: 'red', amount: COLOR_INCREMENT })}
                onDecrease={() => dispatch({ colorToCgange: 'red', amount: -1 * COLOR_INCREMENT })}
                color="Red"
            />
            <ColorCounter 
                onIncrease={() => dispatch({ colorToCgange: 'green', amount: COLOR_INCREMENT })}
                onDecrease={() => dispatch({ colorToCgange: 'green', amount: -1 * COLOR_INCREMENT })}
                color="Green"
            />
            <ColorCounter 
                onIncrease={() => dispatch({ colorToCgange: 'blue', amount: COLOR_INCREMENT })}
                onDecrease={() => dispatch({ colorToCgange: 'blue', amount: -1 * COLOR_INCREMENT})}
                color="Blue"
            />
            <View>
                <View style={{ height: 175, width: 175, backgroundColor: `rgb(${red},${green},${blue})`}} />
    <Text>{`rgb(${red},${green},${blue})`}</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({});

export default SquareScreen;
