import React, { useState } from 'react';
import { View, StyleSheet, Text, Button } from 'react-native';
import ColorCounter from '../components/ColorCounter';

const COLOR_INCREMENT = 15;

const SquareScreen = () => {

    const [ red, setRed ] = useState(0);
    const [ blue, setBlue ] = useState(0);
    const [ green, setGreen ] = useState(0);


    const setColor = (color, change) => {
        switch (color) {
            case 'red':
                red + change > 255 || red + change < 0 ? null : setRed(red + change);
                return;
        }
    }

    return (
        <View>
            <ColorCounter 
                onIncrease={() => setColor('red', COLOR_INCREMENT)}
                onDecrease={() => setColor('red', COLOR_INCREMENT)}
                color="Red"
            />
            <ColorCounter 
                onIncrease={() => setColor('blue', COLOR_INCREMENT)}
                onDecrease={() => setColor('blue', COLOR_INCREMENT)}
                color="Blue"
            />
            <ColorCounter 
                onIncrease={() => setColor('green', COLOR_INCREMENT)}
                onDecrease={() => setColor('green', COLOR_INCREMENT)}
                color="Green"
            />
            <View>
                <View style={{ height: 175, width: 175, backgroundColor: `rgb(${red},${blue},${green})`}} />
    <Text>{`rgb(${red},${blue},${green})`}</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({});

export default SquareScreen;
