import React, { useState } from 'react';
import { View, StyleSheet, Text, Button } from 'react-native';
import ColorCounter from '../components/ColorCounter';

const SquareScreen = () => {

    const [ red, setRed ] = useState(0);
    const [ blue, setBlue ] = useState(0);
    const [ green, setGreen ] = useState(0);

    return (
        <View>
            <ColorCounter 
                onIncrease={() => setRed(red + 3)}
                onDecrease={() => setRed(red - 3)}
                color="Red"
            />
            <ColorCounter 
                onIncrease={() => setBlue(blue + 3)}
                onDecrease={() => setBlue(blue - 3)}
                color="Blue"
            />
            <ColorCounter 
                onIncrease={() => setGreen(green + 3)}
                onDecrease={() => setGreen(green - 3)}
                color="Green"
            />
            <View style={{ height: 175, width: 175, backgroundColor: `rgb(${red},${blue},${green})`}}></View>
        </View>
    )
}

const styles = StyleSheet.create({});

export default SquareScreen;
